import sqlite3; conn = sqlite3.connect("data/reviewtalk.db"); cursor = conn.cursor(); cursor.execute("SELECT COUNT(*) FROM special_products"); print(f"특가 상품 수: {cursor.fetchone()[0]}개"); cursor.execute("SELECT product_name, price, discount_rate, is_crawled, review_count FROM special_products LIMIT 6"); products = cursor.fetchall(); print("\n📦 특가 상품 목록:"); [print(f"{i+1}. {name[:30]}... | 가격: {price} | 할인: {discount} | 리뷰크롤링: {bool(crawled)} | 리뷰수: {reviews}") for i, (name, price, discount, crawled, reviews) in enumerate(products)]; conn.close()
