import sqlite3; conn = sqlite3.connect("./reviewtalk.db"); cursor = conn.cursor(); cursor.execute("SELECT COUNT(*) FROM special_products"); print(f"특가 상품 총 개수: {cursor.fetchone()[0]}개"); cursor.execute("SELECT COUNT(*) FROM special_products WHERE is_crawled = 1"); print(f"리뷰 크롤링 완료: {cursor.fetchone()[0]}개"); cursor.execute("SELECT product_name, review_count, is_crawled FROM special_products ORDER BY review_count DESC LIMIT 5"); products = cursor.fetchall(); print("\n📦 특가 상품 리뷰 수집 현황:"); [print(f"- {name[:40]}... | 리뷰: {reviews}개 | 크롤링: {bool(crawled)}") for name, reviews, crawled in products]; conn.close()
