import sqlite3; conn = sqlite3.connect("data/reviewtalk.db"); cursor = conn.cursor(); cursor.execute("SELECT name FROM sqlite_master WHERE type=\"table\""); tables = cursor.fetchall(); print("📊 데이터베이스 테이블들:"); [print(f"  - {table[0]}") for table in tables]; cursor.execute("SELECT COUNT(*) FROM products"); print(f"\n📦 products 테이블의 상품 수: {cursor.fetchone()[0]}개"); conn.close()
